  //     const response = await axios.post('http://localhost:5000/getRedNo', { OAN });
  //     const redNo = response.data.RedNo;

  //     // Redirect to the ColorTiles component with the 'RedNo' parameter
  //     window.location.href = '/colorTiles/${redNo}';
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  useEffect(() => {
    axios.get('http://localhost:5000/datacustomer')
      .then(response => setData(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <table className="user-table">
        <thead>
          <tr>
            <th>ClientID</th>
            <th>ClientName</th>
            <th>OAN</th>
            <th>Completion Status</th>
          </tr>
        </thead>

        <tbody>
          {data.map((item) => (
            <tr key={item.ClientID} >
              <td>{item.ClientID}</td>
              <td>{item.ClientName}</td>
              <td>{item.OAN}</td>
              <td>{item.Percentage}%</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CustomerTable;
