import React, { useState, useEffect } from 'react';
import './clientViewer.css';
import logo from './Saint-Gobain_SEFPRO_logo_2023.png';
import ProgBar from './ProgBar';
import ProgPerc from './ProgPerc';
import { useNavigate } from 'react-router-dom';

function ClientViewer() {
    const [showDialog, setShowDialog] = useState(false);
    const [percentage, setPercentage] = useState(0);
    const navigate = useNavigate();
    const openDialog = () => {
        navigate('/login');
      };
  const closeDialog = () => {
    setShowDialog(false);
  };
  useEffect(() => {
    // Fetch the percentage from the server when the client logs in
    // You can make an API request to your server here

    // For example, using the fetch API
    fetch('http://localhost:5000/api/getPercentage', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      // Pass any necessary data, like the user ID, in the request body
      body: JSON.stringify({ userID: 'C1' }),
    })
      .then((response) => response.json())
      .then((data) => {
        // Update the percentage value in the state
        setPercentage(data.percentage);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []); // This effect runs once when the component is mounted

  return (
    <div className="Container">
      <div className="Header" style={{ display: 'flex' }}>
        <div className="logo">
          <img src={logo} alt="logo" style={{ width: '170px', height: '100px' }} />
        </div>
        <div className="pages">
          <p className='home' style={{ color: '#00C7C8' }}>Track Progress</p>
        </div>
        <div >
            <button className="logout" style={{ marginRight: '1%' }} onClick={() => setShowDialog(true)}>
          Logout</button>
        </div>
      </div>
      {showDialog && (
      <div className="dialog">
        <label className='confirmLogout'>Confirm Logout</label>
        <div className='buttons'>
        <button onClick={openDialog}>YES</button>
        <button className='yesbtn' onClick={closeDialog}>NO</button>
        </div>
      </div>
    )}
        <div className="view">
          
        </div>
        <div className="actperc">
        <ProgPerc calculatedValue={percentage} maxValue={100} />
        </div>
        <div className="actblock">
          <ProgBar/> 
        </div>
    </div>

    
  );
}

export default ClientViewer;
