import React, { useState } from 'react';
//import { useNavigate } from 'react-router-dom';
import './customers.css';
import logo from './Saint-Gobain_SEFPRO_logo_2023.png';
import ProgBar from './modelProgBar';
import CustomerTable from './customerTable';
import ProgBar2 from './modelProgBar2';
import ProgBar3 from './modelProgBar3';
//import ProgPerc from './ProgPerc';

function CustomerDashBoard() {
    const [showDialog, setShowDialog] = useState(false);
    //const navigate = useNavigate();
    // const navigateToUser = () => {
    //   navigate('/');
    // };
    const openDialog = () => {
        setShowDialog(true);
      };
  const closeDialog = () => {
    setShowDialog(false);
  };

  return (
    <div className="Container">
      <div className="Header" style={{ display: 'flex' }}>
        <div className="logo">
          <img src={logo} alt="logo" style={{ width: '170px', height: '100px' }} />
        </div>
        <div className="pages">
          <p className='headerText'>Home</p>
        </div>
        <div className="pages">
          <p className='headerText'>New Plan</p>
        </div>
        <div className="pages">
          <p className='headerText'>Track Progress</p>
        </div>
        <div className="pages">
          <p className='headerText' >Users</p>
        </div>
        <div className="pages">
          <p className='home' style={{ color: '#00C7C8' }}>Customers</p>
        </div>
        <div >
            <button className="logout" style={{ marginRight: '1%' }} onClick={() => setShowDialog(true)}>
          Logout</button>
        </div>
      </div>
      {showDialog && (
      <div className="dialog">
        <label className='confirmLogout'>Confirm Logout</label>
        <div className='buttons'>
        <button onClick={openDialog}>YES</button>
        <button className='yesbtn' onClick={closeDialog}>NO</button>
        </div>
      </div>
    )}
        <br/>
        <div className='container'>
            <div className="actblock1">
            <ProgBar/> 
            </div>
            <div className="actblock2">
            <ProgBar2/> 
            </div>
            <div className="actblock3">
            <ProgBar3/> 
            
            </div>
           
            
        </div>
        <br/>
            <div className='ct' style={{ width: '100%' }}>
            <CustomerTable/>
            </div>
    </div>

    
  );
}

export default CustomerDashBoard;
